package com.example.rybalnya.ui.chat

import android.os.Bundle
import android.util.Log
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Toast
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.rybalnya.BASE_URL
import com.example.rybalnya.MainActivity2
import com.example.rybalnya.R
import com.example.rybalnya.adapters.UserAdapter
import com.example.rybalnya.api.ApiRequests
import com.example.rybalnya.api.UserRecieve
import com.example.rybalnya.models.UserModel
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.GlobalScope
import kotlinx.coroutines.launch
import retrofit2.Retrofit
import retrofit2.awaitResponse
import retrofit2.converter.gson.GsonConverterFactory


class FriendsFragment : Fragment() {

    private lateinit var recyclerView: RecyclerView
    private lateinit var userAdapter: UserAdapter
    private lateinit var friends: ArrayList<UserModel>
    private lateinit var userRecive: ArrayList<UserRecieve>
    private lateinit var onlineRecive: ArrayList<Boolean>
    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        val view = inflater.inflate(R.layout.fragment_friends, container, false)
        recyclerView = view.findViewById(R.id.recycler_view_friends)
        recyclerView.setHasFixedSize(true)
        recyclerView.layoutManager = LinearLayoutManager(context)

        friends = ArrayList()

        getFriends(MainActivity2().getTkn().toString())

        return view
    }

    private fun getFriends(token: String) {
//        TODO("Make friend list in DB and implement retrieving them")
        var api: ApiRequests? = null
        try {
            api = Retrofit.Builder()
                .baseUrl(BASE_URL)
                .addConverterFactory(GsonConverterFactory.create())
                .build()
                .create(ApiRequests::class.java)
        } catch (e: Exception) {
            Log.i("ApiRequest", e.toString())
        }
        GlobalScope.launch(Dispatchers.Main) {
            try {
                val response = api?.getAllUsersInfoWithOnline(token)?.awaitResponse()

                Log.i("Response", response.toString())
                Log.i("Response body", response?.body().toString())
                if (response?.code() == 200) {
                    Log.i("getUsersWithOnline", "Successful")
                    Toast.makeText(
                        this@FriendsFragment.requireActivity(),
                        response.body()?.Action.toString(),
                        Toast.LENGTH_SHORT
                    ).show()

                    if (response.body()?.Users != null && response.body()?.Online != null) {
                        userRecive = ArrayList()
                        userRecive = response.body()!!.Users!!
                        onlineRecive = ArrayList()
                        onlineRecive = response.body()!!.Online!!

                    } else {
                        Toast.makeText(
                            this@FriendsFragment.requireActivity(),
                            "Пользователи не найдены",
                            Toast.LENGTH_SHORT
                        ).show()
                    }
                    var i = 0
                    for (user in userRecive) {
                        if (user.FullName == "") {
                            user.FullName = user.Email
                        }
                        var isOnline = "offline"
                        if (onlineRecive[i] == true) {
                            isOnline = "online"
                        }
                        friends.add(
                            UserModel(
                                user.FullName,
                                user.Nickname,
                                user.Email,
                                user.Image,
                                isOnline
                            )
                        )
                        i++
                    }

                    recyclerView.adapter = userAdapter

                } else if (response?.code() == 400 || response?.code() == 401) {
                    Log.i("getAllUsersInfo", "Something went wrong")
                    Toast.makeText(
                        this@FriendsFragment.requireActivity(),
                        response.body()?.Error.toString(),
                        Toast.LENGTH_SHORT
                    ).show()
                }
            } catch (e: Exception) {
                Log.i("getAllUsersInfo", e.toString())
            }
        }
//        friends.add(
//            UserModel(
//                "hi",
//                "jopa",
//                "dd@d.f",
//                "",
//                "online"
//            )
//        )
//        friends.add(
//            UserModel(
//                null,
//                "you",
//                "dd@d.f",
//                "default",
//                "online"
//            )
//        )
//            friends.add(UserModel(null, null, "dd@d.f", "default", "offline"))
        userAdapter = UserAdapter(requireContext(), friends, false)
        recyclerView.adapter = userAdapter
    }

}